      "contents": "import express from \"express\";\nconst app = express();\nconst port = 3111;\n\nimport { parser } from \"./parser.js\";\n\nconst html = `\n<div>\n  <h2 id=\"build\">ÊòØÂÖÑÂºüÂ∞±Êù•ÁÇπÊàëÔºÅ</h2>\n  <div id=\"output\">\n    <h1>Welcome to a TSaid! ü•≥</h1>\n  </div>\n</div>\n\n<script type=\"module\">\n  import { codeToHtml } from \"https://esm.sh/shiki@1.0.0\";\n  const build = document.querySelector(\"#build\");\n  const out = document.querySelector(\"#output\");\n  build.addEventListener(\"click\", () => {\n    fetch(\"/rerun\")\n      .then((resp) => resp.json())\n      .then((resp) => {\n        codeToHtml(resp.ans, {\n          lang: \"json\",\n          theme: \"min-light\",\n        }).then((html) => {\n          out.innerHTML = html;\n        });\n      });\n  });\n  setTimeout(() => {\n    console.log(\"click ÂÖÑÂºü\")\n    build.click()\n  }, 500)\n\n</script>\n\n\n`;\n\napp.get(\"/\", (req, res) => {\n  res.send(html);\n});\n\napp.get(\"/rerun\", (req, res) => {\n  let ans = \"NONE\";\n  try {\n    ans = parser();\n  } catch (error) {\n    ans = error.toString();\n  }\n  res.json({ ans });\n});\n\napp.listen(port, () => {\n  console.log(`App is live at http://localhost:${port}`);\n});\n"
